import React, { useState, useEffect } from 'react';
import ProductCard from '../ProductCard/ProductCard';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);

  useEffect(() => {
    // Fetch products from the API endpoint
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('https://enock-scandiweb-api.idealcis.com');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleCheckboxChange = (productId) => {
    setSelectedProducts((prevSelected) => {
      if (prevSelected.includes(productId)) {
        return prevSelected.filter((id) => id !== productId);
      } else {
        return [...prevSelected, productId];
      }
    });
  };
  const handleMassDelete = async () => {
    try {
      // Delete the selected products using the API endpoint
      await Promise.all(
        selectedProducts.map((productId) =>
          fetch(`https://enock-scandiweb-api.idealcis.com/${productId}`, {
            method: 'DELETE',
          })
        )
      );
      console.log('Mass deletion successful');
      // Refresh the product list after successful mass deletion
      fetchProducts();
      // Clear the selectedProducts array
      setSelectedProducts([]);
    } catch (error) {
      console.error('Error during mass deletion:', error);
    }
  };


  return (
    <div className="container">
      <div className="row">
        {products.map((product) => (
          <div className="col-md-3" key={product.id}>
            <ProductCard
              product={product}
              isSelected={selectedProducts.includes(product.id)}
              onCheckboxChange={() => handleCheckboxChange(product.id)}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
