import React from "react";
import { Link, useLocation } from 'react-router-dom';
import AddProduct from '../../Pages/AddProduct'
import ProductList from '../../Pages/Products';
import './Navbar.css'

function Navbar() {
  const location = useLocation();

  // Check if the current URL contains "/addproduct"
  const isAddProductPage = location.pathname.includes('/addproduct');

  return (
    <Router>
      <nav className="navbar navbar-expand-lg " data-bs-theme="dark">
        <div className="container-fluid">
          <ul>
            <li className="logo nav-header  custom-link">
              {isAddProductPage ? (
                <Link to="/addproduct">Product Add</Link>
              ) : (
                <Link to="/">Product List</Link>
              )}
            </li>
          </ul>
          <ul className="nav nav-pills margin-right">
            {isAddProductPage ? (
              <>
                <li className="nav-item custom-link">
                  <Link className="btn btn-primary" to="/">Save</Link>
                </li>
                <li className="nav-item ms-1  custom-link">
                  <Link className="btn btn-warning" to="/">Cancel</Link>
                </li>
              </>
            ) : (
              <>
                <li className="nav-item custom-link">
                  <Link className="btn btn-success" to="/addproduct">ADD</Link>
                </li>
                <li className="nav-item ms-1  custom-link">
                  <button className="btn btn-danger" id="delete-product-btn">MASS DELETE</button>
                </li>
              </>
            )}
          </ul>
        </div>
      </nav>
      <div className="line"></div>
      <Routes>
        <Route path="/" element={<ProductList />} />
        <Route path="/addproduct" element={<AddProduct />} />
      </Routes>
      <div className="footer">
        <div className="line"></div>
        <h4>Scandiweb Test Assignment</h4>
      </div>
    </Router>
  )
}

export default Navbar;
