import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import AddProduct from '../../Pages/AddProduct'
import ProductList from '../../Pages/Products';
import './Navbar.css'

function Navbar(){
    const [selectedProducts, setSelectedProducts] = useState([]);
    const handleMassDelete = () => {
        // Perform the mass delete operation using the API endpoint or any other logic
        console.log('Deleting selected products:', selectedProducts);
      };


    return (
        <Router>
            <nav className="navbar navbar-expand-lg " data-bs-theme="dark">
                <div className="container-fluid">
                    <ul>
                    <li className="logo nav-header  custom-link">
                        <Link to="/">Product List</Link>
                    </li>
                    </ul>
                    <ul className="nav nav-pills margin-right">

                        <li className="nav-item custom-link">
                            <Link className="btn btn-success" to="/addproduct">ADD</Link>
                        </li>
                        <li className="nav-item ms-1  custom-link">
                        <button className="btn btn-danger" onClick={handleMassDelete} disabled={selectedProducts.length === 0}>MASS DELETE</button>
                        </li>
                    </ul>

                </div>
            </nav>
            <div class="line"></div>
        <Routes>
        <Route path="/" element={<ProductList />} />
        <Route path="/addproduct" element={<AddProduct />} />
        </Routes>
        <div className="footer">
        <div class="line"></div>
            <h4>Scandiweb Test Assignment</h4>

        </div>


        </Router>
    )
}

export default Navbar;
