import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import Navbar from './components/Navigation/Navbar';
import Header from './components/Navigation/Header';

function App() {
  const [selectedProducts, setSelectedProducts] = useState([]);
  const handleMassDelete = async () => {
    try {
      // Delete the selected products using the API endpoint
      await Promise.all(
        selectedProducts.map((productId) =>
          fetch(`https://enock-scandiweb-api.idealcis.com/${productId}`, {
            method: 'DELETE',
          })
        )
      );
      console.log('Mass deletion successful');
      // Refresh the product list after successful mass deletion
      fetchProducts();
      // Clear the selectedProducts array
      setSelectedProducts([]);
    } catch (error) {
      console.error('Error during mass deletion:', error);
    }
  };
  return (
    <Router>
      <Switch>
        <Route exact path="/" render={() => <Navbar selectedProducts={selectedProducts} handleMassDelete={handleMassDelete}/>} />
        <Route exact path="/addproduct" render={() => <Header />} />
      </Switch>
      <Switch>
        <Route exact path="/"  />
        <Route exact path="/addproduct" />
      </Switch>
    </Router>
  );
}

export default App;
