import React, { useState } from "react";
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar({ selectedProducts, handleMassDelete }) {
  const [isMassDeleteConfirmationOpen, setIsMassDeleteConfirmationOpen] = useState(false);

  const toggleMassDeleteConfirmation = () => {
    setIsMassDeleteConfirmationOpen((prevState) => !prevState);
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg" data-bs-theme="dark">
        <div className="container-fluid">
          <ul className="navbar-nav">
            <li className="nav-item logo nav-header custom-link">
              <Link to="/">Product List</Link>
            </li>
          </ul>
          <ul className="nav nav-pills margin-right">
            <li className="nav-item custom-link">
              <Link className="btn btn-success" to="/addproduct">ADD</Link>
            </li>
            <li className="nav-item ms-1 custom-link">
              <button
                className={`btn btn-danger ${selectedProducts.length === 0 ? "disabled" : ""}`}
                onClick={toggleMassDeleteConfirmation}
                disabled={selectedProducts.length === 0}
              >
                MASS DELETE
              </button>
            </li>
          </ul>
        </div>
      </nav>

      {isMassDeleteConfirmationOpen && (
        <div className="mass-delete-confirmation">
          <div className="mass-delete-confirmation-content">
            <h5>Are you sure you want to delete the selected products?</h5>
            <div className="mass-delete-buttons">
              <button className="btn btn-danger" onClick={handleMassDelete}>
                Confirm Delete
              </button>
              <button className="btn btn-secondary" onClick={toggleMassDeleteConfirmation}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="line"></div>
      <br></br>
    </>
  );
}

export default Navbar;
