import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch, useLocation } from 'react-router-dom';
import Navbar from './components/Navigation/Navbar';
import Header from './components/Navigation/Header';
import Products from './Pages/Products';
import AddProduct from './Pages/AddProduct';

function App() {
  const location = useLocation();

  // Check if the current location is "/addproduct"
  const isAddProductPage = location.pathname === '/addproduct';

  return (
    <Router>
      <div>
        {location.pathname === '/' && <Navbar />} {/* Render Navbar only on the "/" route */}
        {isAddProductPage && <Header />} {/* Render Header only on the "/addproduct" route */}
        <Switch>
          <Route path="/addproduct">
            <AddProduct />
          </Route>
          <Route path="/">
            <Products />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
