import React from 'react';
import ProductCard from '../ProductCard/ProductCard';

const ProductList = ({ products, fetchProducts, selectedProducts, onCheckboxChange }) => {
  if (!products) {
    return <div>Loading...</div>; // You can show a loading indicator or a message while the products are being fetched
  }

  return (
    <div className="container">
      <div className="row">
        {products.map((product) => (
          <div className="col-md-3" key={product.id}>
            <ProductCard
              product={product}
              isSelected={selectedProducts.includes(product.id)}
              onCheckboxChange={() => onCheckboxChange(product.id)}
              fetchProducts={fetchProducts}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
