import './App.css';
import { BrowserRouter as Router, Route, useLocation } from 'react-router-dom';
import Navbar from './components/Navigation/Navbar';
import Header from './components/Navigation/Header';

function App() {
  return (
    <Router>
      <Navbar />
      <PageContent />
    </Router>
  );
}

// Component that renders the appropriate content based on the current route
const PageContent = () => {
  const location = useLocation();

  return (
    <>
      <Route exact path="/">
        <HomePage />
      </Route>
      <Route path="/addproduct">
        {location.pathname === "/addproduct" && <Header />}
      </Route>
    </>
  );
};

// Homepage component
const HomePage = () => {
  return <h1>Home Page</h1>;
};

export default App;
